mapper.select:
SELECT * FROM km_checkin_record
WHERE checkin_item_id = #{checkinItemId}
  AND username = #{userId}
  AND cycle_key = #{cycleKey}
  AND arch_flag = 0
LIMIT 1

mapper.delete:
DELETE FROM km_checkin_record
WHERE checkin_item_id = #{checkinItemId}
  AND username = #{userId}
  AND cycle_key = #{cycleKey}
  AND arch_flag = 0
