mapper.select:
SELECT 
FROM km_custom_practice
WHERE id = #{id} AND arch_flag = 0 AND rec_creator = #{recCreator}

mapper.update:
UPDATE km_custom_practice
practice_name = #{practiceName}, scenario_type = #{scenarioType}, scenario_detail = #{scenarioDetail}, difficulty = #{difficulty}, background = #{background}, description = #{description}, target_skills = #{targetSkills}, tech_stack = #{techStack}, constraints = #{constraints}, practice_content = #{practiceContent}, evaluation_criteria = #{evaluationCriteria}, reference_materials = #{referenceMaterials}, time_limit = #{timeLimit}, compatibility_notes = #{compatibilityNotes}, tags = #{tags}, rec_revisor = #{recRevisor}, rec_revise_time = NOW()
WHERE id = #{id}

mapper.delete:
UPDATE km_custom_practice
SET arch_flag = 1, rec_revise_time = NOW()
WHERE id = #{id}
