mapper.select:
SELECT * FROM km_guidance_outline
WHERE id = #{id}
AND rec_creator = #{recCreator}
AND arch_flag = 0

SELECT * FROM km_guidance_outline
WHERE rec_creator = #{recCreator}
AND arch_flag = 0

    AND subject_category = #{subjectCategory}

ORDER BY rec_create_time DESC

mapper.update:
UPDATE km_guidance_outline
SET arch_flag = 1
WHERE id = #{id}
