mapper.select:
SELECT 
FROM km_honor
WHERE id = #{id} AND arch_flag = 0 AND rec_creator = #{recCreator}

mapper.update:
UPDATE km_honor
title = #{title}, description = #{description}, category = #{category}, achieved_time = #{achievedTime}, project_id = #{projectId}, icon = #{icon}, rec_revisor = #{recRevisor}, rec_revise_time = NOW()
WHERE id = #{id}

mapper.delete:
UPDATE km_honor
SET arch_flag = 1, rec_revise_time = NOW()
WHERE id = #{id}
