mapper.select:
SELECT 
FROM km_note
WHERE id = #{id} AND arch_flag = 0 AND rec_creator = #{recCreator}

mapper.update:
UPDATE km_note
type = #{type}, title = #{title}, summary = #{summary}, content = #{content}, tags = #{tags}, rec_revisor = #{recRevisor}, rec_revise_time = NOW()
WHERE id = #{id}

mapper.delete:
UPDATE km_note
SET arch_flag = 1, rec_revise_time = NOW()
WHERE id = #{id}
