mapper.select:
SELECT 
FROM km_project
WHERE id = #{id} AND arch_flag = 0 AND rec_creator = #{recCreator}

mapper.update:
UPDATE km_project
name = #{name}, description = #{description}, status = #{status}, category = #{category}, task_count = #{taskCount}, rec_revisor = #{recRevisor}, rec_revise_time = NOW()
WHERE id = #{id}

mapper.delete:
UPDATE km_project
SET arch_flag = 1, rec_revise_time = NOW()
WHERE id = #{id}

mapper.update:
UPDATE km_project
SET task_count = #{taskCount}, rec_revise_time = NOW()
WHERE id = #{id}
