mapper.select:
SELECT 
FROM km_project_template
WHERE id = #{id} AND arch_flag = 0 AND rec_creator = #{recCreator}

mapper.update:
UPDATE km_project_template
domain = #{domain}, name = #{name}, description = #{description}, requirements = #{requirements}, common_features = #{commonFeatures}, rec_revisor = #{recRevisor}, rec_revise_time = NOW()
WHERE id = #{id}

mapper.delete:
UPDATE km_project_template
SET arch_flag = 1, rec_revise_time = NOW()
WHERE id = #{id}
