mapper.insert:
INSERT INTO km_requirement_draft (
    version, title, description, status, sort_order,
    rec_creator, rec_create_time, rec_revisor, rec_revise_time, arch_flag
) VALUES (
    #{version}, #{title}, #{description}, #{status}, #{sortOrder},
    #{recCreator}, #{recCreateTime}, #{recRevisor}, #{recReviseTime}, #{archFlag}
)

mapper.update:
UPDATE km_requirement_draft
version = #{version}, title = #{title}, description = #{description}, status = #{status}, sort_order = #{sortOrder}, rec_revisor = #{recRevisor}, rec_revise_time = #{recReviseTime},
WHERE id = #{id}

UPDATE km_requirement_draft
SET arch_flag = 1
WHERE id = #{id}
