mapper.select:
SELECT 
FROM km_summary
WHERE id = #{id} AND arch_flag = 0 AND rec_creator = #{recCreator}

mapper.update:
UPDATE km_summary
title = #{title}, type = #{type}, period = #{period}, description = #{description}, content = #{content}, satisfaction = #{satisfaction}, tags = #{tags}, rec_revisor = #{recRevisor}, rec_revise_time = NOW()
WHERE id = #{id}

mapper.delete:
UPDATE km_summary
SET arch_flag = 1, rec_revise_time = NOW()
WHERE id = #{id}
