mapper.select:
SELECT 
FROM km_task
WHERE id = #{id} AND arch_flag = 0 AND rec_creator = #{recCreator}

mapper.update:
UPDATE km_task
name = #{name}, description = #{description}, status = #{status}, deadline = #{deadline}, completed_time = #{completedTime}, new_progress = #{newProgress}, rec_revisor = #{recRevisor}, rec_revise_time = NOW()
WHERE id = #{id}

mapper.delete:
UPDATE km_task
SET arch_flag = 1, rec_revise_time = NOW()
WHERE id = #{id}
