mapper.select:
SELECT 
FROM km_time_node
WHERE id = #{id} AND arch_flag = 0 AND rec_creator = #{recCreator}

mapper.update:
UPDATE km_time_node
title = #{title}, description = #{description}, time = #{time}, type = #{type}, status = #{status}, rec_revisor = #{recRevisor}, rec_revise_time = NOW()
WHERE id = #{id}

mapper.delete:
UPDATE km_time_node
SET arch_flag = 1, rec_revise_time = NOW()
WHERE id = #{id}
